// Extract updated draft from the Teams message HTML (<pre>...</pre>) and decode entities
const html = $json.body?.content || '';
let updatedDraft = '';
const m = html.match(/<pre[^>]*>([\s\S]*?)<\/pre>/i);
if (m) {
  updatedDraft = m[1]
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&amp;/g, '&')
    .replace(/&?/g, '')
    .trim();
}
return { updatedDraft };

// Get the raw string from the previous node's error path
const rawString = $json.error;

// Use a regular expression to reliably find the JSON content, even with surrounding text
const jsonMatch = rawString.match(/\{[\s\S]*\}/);

// If no JSON object is found, throw an error
if (!jsonMatch) {
  throw new Error("Could not find a valid JSON object in the input string.");
}

let jsonString = jsonMatch[0];

// **THIS IS THE KEY FIX**
// Before parsing, replace unescaped control characters with their valid escaped versions.
// 1. Replace backslashes: \ -> \\
// 2. Replace newlines: \n -> \\n
// 3. Replace carriage returns: \r -> \\r
jsonString = jsonString.replace(/\\/g, "\\\\")
                       .replace(/\n/g, "\\n")
                       .replace(/\r/g, "\\r");

// Parse the now-clean and valid JSON string
// This object will be the output of this node.
return JSON.parse(jsonString);

// Get the input data from the previous node.
const inputData = $input.first().json;

// Initialize arrays to hold the categorized emails.
const inboundMails = [];
const outboundMails = [];

// Ensure the 'mails' array exists before processing.
if (inputData.content && Array.isArray(inputData.content.mails)) {
  // Loop through each mail object in the input array.
  for (const mail of inputData.content.mails) {
    // Create a new, simplified object with only the required fields.
    const simplifiedMail = {
      senderName: mail.senderName,
      subject: mail.subject,
      bodyPlain: mail.bodyPlain,
    };

    // Check the 'inbound' property to categorize the mail.
    if (mail.inbound === true) {
      inboundMails.push(simplifiedMail);
    } else {
      outboundMails.push(simplifiedMail);
    }
  }
}

// Prepare the final output object.
const output = {
  inboundMails,
  outboundMails,
};

// Return the structured data for the next node in the workflow.
// n8n expects an array of items, each with a 'json' property.
return [{
  json: output
}];
